import React, { useState } from 'react';

const App = () => {
    // State to toggle visibility of product items
    const [showProducts, setShowProducts] = useState(false);

    // Function to show the product items
    const showProductList = () => {
        setShowProducts(true);
    };

    // Function to hide the product items
    const hideProductList = () => {
        setShowProducts(false);
    };

    return (
        <div>
            <header>
                <div className="nav-buttons">
                    <button>Home</button>
                    {/* Add mouse event handlers to show/hide product list */}
                    <button 
                        onMouseEnter={showProductList} 
                        onMouseLeave={hideProductList}
                    >
                        Products <span style={{ cursor: 'pointer' }}>â–¼</span>
                    </button>
                    <button>Pricing</button>
                </div>

                {/* User Profile Section */}
                <div className="user-profile">
                    <img src="https://thumbs.dreamstime.com/b/profile-pic-icon-isolated-white-background-your-web-mobile-app-design-133862807.jpg" alt="User Avatar" className="avatar" />
                    <span className="username">Chinmay</span>
                </div>
            </header>

            <main>
                <h1>Beautiful analytics to grow smarter</h1>
                <p className="subheading">
                    Powerful, self-serve product and growth analytics to help you convert, engage, and retain more users. Trusted by over 4,000 startups.
                </p>

                {/* Buttons below the heading */}
                <div className="action-buttons">
                    <button className="demo-btn">Demo</button>
                    <button className="signup-btn">Sign Up</button>
                </div>

                {/* Image after Sign Up Button */}
                <div className="image-container">
                    <img 
                        src="https://hoist.digital/user/pages/06.content/blog/how-to-grow-your-business-with-google-analytics/How_to_grow_your_business_with_Google_Analytics_Browser_Click_on_Tech.png" 
                        alt="How to grow your business with Google Analytics" 
                        className="promo-image"
                    />
                </div>

                {/* Conditionally render product items based on showProducts state */}
                {showProducts && (
                    <div className="product-list">
                        <ul>
                            <li>Product 1</li>
                            <li>Product 2</li>
                            <li>Product 3</li>
                        </ul>
                    </div>
                )}
            </main>
        </div>
    );
};

export default App;
